import React, { useState, useEffect } from "react";
import { Task } from "../../../interfaces";
import { ReactComponent as Calendar } from "../../../assets/date.svg";
import useDate from "../../hooks/useDate";

// Function to get domain name and replace dots with spaces
const getDomainName = (url: string) => {
  try {
    const hostname = new URL(url).hostname;
    // Remove www. prefix if exists
    const domain = hostname.replace(/^www\./, '');
    // Replace dots with spaces
    return domain.split('.').slice(0, -1).join(' ');
  } catch (error) {
    console.error("Error extracting domain name:", error);
    return "Invalid URL";
  }
};

// Function to format elapsed time
const formatElapsedTime = (startTime: number) => {
  const now = Date.now();
  const elapsed = now - startTime;
  const seconds = Math.floor(elapsed / 1000) % 60;
  const minutes = Math.floor(elapsed / (1000 * 60)) % 60;
  const hours = Math.floor(elapsed / (1000 * 60 * 60)) % 24;
  const days = Math.floor(elapsed / (1000 * 60 * 60 * 24));

  return `${days}d ${hours}h ${minutes}m ${seconds}s`;
};

const InfosTask: React.FC<{ task: Task; isListInView1: boolean }> = ({
  task,
  isListInView1,
}) => {
  const [startTime, setStartTime] = useState<number>(task.date ? new Date(task.date).getTime() : 0);
  const [elapsedTime, setElapsedTime] = useState(() => formatElapsedTime(startTime));
  const [isTimerRunning, setIsTimerRunning] = useState<boolean>(true);

  const dateFormatted = useDate(task.date || ""); // Handle empty date

  useEffect(() => {
    let intervalId: NodeJS.Timeout;

    if (isTimerRunning) {
      intervalId = setInterval(() => {
        setElapsedTime(formatElapsedTime(startTime));
      }, 1000);
    }

    return () => clearInterval(intervalId);
  }, [isTimerRunning, startTime]);

  const handleStart = () => setIsTimerRunning(true);
  const handleStop = () => setIsTimerRunning(false);
  const handleReset = () => {
    setStartTime(Date.now());
    setElapsedTime('0d 0h 0m 0s');
    setIsTimerRunning(true);
  };

  return (
    <div className={`flex flex-col flex-1 ${isListInView1 ? "mr-6" : ""}`}>
      <div className={`flex items-center justify-between ${isListInView1 ? "mb-1" : "mb-2"}`}>
        <span className="block font-medium dark:text-slate-200">
          {task.title}
        </span>
      </div>
      <a
        href={task.link}
        title={getDomainName(task.link)}
        className={`description mb-2 text-slate-500 dark:text-slate-500 ${isListInView1 ? "line-clamp-2 sm:line-clamp-1" : "line-clamp-3"}`}
        target="_blank"
        rel="noopener noreferrer"
        aria-label={getDomainName(task.link)}
      >
        {getDomainName(task.link)}
      </a>
      {task.link1 && (
        <a
          href={task.link1}
          title={getDomainName(task.link1)}
          className="block mb-2 text-slate-500 dark:text-slate-500"
          target="_blank"
          rel="noopener noreferrer"
          aria-label={getDomainName(task.link1)}
        >
          {getDomainName(task.link1)}
        </a>
      )}
      {task.link2 && (
        <a
          href={task.link2}
          title={getDomainName(task.link2)}
          className="block mb-2 text-slate-500 dark:text-slate-500"
          target="_blank"
          rel="noopener noreferrer"
          aria-label={getDomainName(task.link2)}
        >
          {getDomainName(task.link2)}
        </a>
      )}
      {task.link3 && (
        <a
          href={task.link3}
          title={getDomainName(task.link3)}
          className="block mb-2 text-slate-500 dark:text-slate-500"
          target="_blank"
          rel="noopener noreferrer"
          aria-label={getDomainName(task.link3)}
        >
          {getDomainName(task.link3)}
        </a>
      )}
      {dateFormatted && (
        <time className="mt-auto flex w-full">
          <Calendar className="mr-2 w-4 sm:w-5" /> {dateFormatted}
        </time>
      )}
      <div className="mt-2 text-slate-500 dark:text-slate-500">
        Elapsed Time: {elapsedTime}
      </div>
      <div className="mt-4 flex space-x-2">
        <button
          onClick={handleStart}
          className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        >
          Start
        </button>
        <button
          onClick={handleStop}
          className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
        >
          Stop
        </button>
        <button
          onClick={handleReset}
          className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
        >
          Reset
        </button>
      </div>
    </div>
  );
};

export default InfosTask;
