import React, { useEffect, useState } from "react";
import { Task } from "../../../interfaces";
import { ReactComponent as Calendar } from "../../../assets/date.svg";
import useDate from "../../hooks/useDate";

// Function to get domain name and replace dots with spaces
const getDomainName = (url: string) => {
  try {
    const hostname = new URL(url).hostname;
    // Remove www. prefix if exists
    const domain = hostname.replace(/^www\./, '');
    // Replace dots with spaces
    return domain.split('.').slice(0, -1).join(' ');
  } catch (error) {
    console.error("Error extracting domain name:", error);
    return "Invalid URL";
  }
};

// Function to format the timer in hours:minutes:seconds
const formatTime = (totalSeconds: number) => {
  const hours = Math.floor(totalSeconds / 3600);
  const minutes = Math.floor((totalSeconds % 3600) / 60);
  const seconds = totalSeconds % 60;

  return `${String(hours).padStart(2, '0')}:${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;
};

const InfosTask: React.FC<{ task: Task; isListInView1: boolean }> = ({
  task,
  isListInView1,
}) => {
  const [remainingTime, setRemainingTime] = useState<number>(0);
  const dateFormatted = useDate(task.date || ""); // Handle empty date

  useEffect(() => {
    const key = `endTime_${task.title}`;
    let endTime: number;

    if (task.timer) {
      const savedEndTime = localStorage.getItem(key);
      if (savedEndTime) {
        endTime = parseInt(savedEndTime, 10);
      } else {
        endTime = Date.now() + task.timer * 3600000; // Convert hours to milliseconds
        localStorage.setItem(key, endTime.toString());
      }

      const updateTimer = () => {
        const now = Date.now();
        const timeLeft = Math.max(0, Math.floor((endTime - now) / 1000));
        setRemainingTime(timeLeft);
      };

      // Initial update
      updateTimer();

      // Update every second
      const intervalId = setInterval(updateTimer, 1000);

      // Clear interval on component unmount
      return () => {
        clearInterval(intervalId);
        // Remove item from localStorage when timer expires
        if (remainingTime <= 0) {
          localStorage.removeItem(key);
        }
      };
    }
  }, [task.timer, task.title, remainingTime]);

  return (
    <div className={`flex flex-col ${isListInView1 ? "mr-6" : ""}`}>
      {/* Container for date and title */}
      <div className="flex flex-col mb-2">
        {dateFormatted && (
          <time className="flex items-center mb-1 text-slate-500 dark:text-slate-400">
            <Calendar className="mr-2 w-4 sm:w-5" /> {dateFormatted}
          </time>
        )}
        <span className="block font-medium dark:text-slate-200">
          {task.title}
        </span>
      </div>
      <a
        href={task.link}
        title={getDomainName(task.link)}
        className={`description mb-2 text-slate-500 dark:text-slate-500 ${isListInView1 ? "line-clamp-2 sm:line-clamp-1" : "line-clamp-3"}`}
        target="_blank"
        rel="noopener noreferrer"
        aria-label={getDomainName(task.link)}
      >
        {getDomainName(task.link)}
      </a>
      {task.link1 && (
        <a
          href={task.link1}
          title={getDomainName(task.link1)}
          className="block mb-2 text-slate-500 dark:text-slate-500"
          target="_blank"
          rel="noopener noreferrer"
          aria-label={getDomainName(task.link1)}
        >
          {getDomainName(task.link1)}
        </a>
      )}
      {task.link2 && (
        <a
          href={task.link2}
          title={getDomainName(task.link2)}
          className="block mb-2 text-slate-500 dark:text-slate-500"
          target="_blank"
          rel="noopener noreferrer"
          aria-label={getDomainName(task.link2)}
        >
          {getDomainName(task.link2)}
        </a>
      )}
      {task.link3 && (
        <a
          href={task.link3}
          title={getDomainName(task.link3)}
          className="block mb-2 text-slate-500 dark:text-slate-500"
          target="_blank"
          rel="noopener noreferrer"
          aria-label={getDomainName(task.link3)}
        >
          {getDomainName(task.link3)}
        </a>
      )}
      {task.timer && (
        <div className="mt-2 flex items-center">
          <span className="text-slate-500 dark:text-slate-400">Timer:</span>
          <span className="ml-2">{formatTime(remainingTime)}</span>
        </div>
      )}
    </div>
  );
};

export default InfosTask;
