import React, { useState } from "react";

const Notepad: React.FC = () => {
  const [note, setNote] = useState<string>("");
  const [isEditing, setIsEditing] = useState<boolean>(false);

  const handleChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setNote(event.target.value);
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    setIsEditing(false);
  };

  const handleDelete = () => {
    setNote("");
    setIsEditing(false);
  };

  return (
    <div className="mt-4">
      <h3 className="text-lg font-medium mb-2">Notepad</h3>

      {/* Tampilkan Output Catatan */}
      {!isEditing && (
        <div className="mb-2 p-2 border rounded-md dark:bg-slate-700 dark:text-slate-200">
          {note || "Belum ada catatan."}
        </div>
      )}

      {/* Tampilkan Textarea jika sedang dalam mode edit */}
      {isEditing && (
        <textarea
          value={note}
          onChange={handleChange}
          placeholder="Tulis catatanmu di sini..."
          className="w-full p-2 border rounded-md dark:bg-slate-700 dark:text-slate-200"
          rows={5}
        />
      )}

      {/* Tombol-tombol Edit, Save, Delete */}
      <div className="flex space-x-2 mt-2">
        {isEditing ? (
          <button
            onClick={handleSave}
            className="bg-green-500 text-white p-2 rounded-md transition hover:bg-green-600"
          >
            Save
          </button>
        ) : (
          <button
            onClick={handleEdit}
            className="bg-blue-500 text-white p-2 rounded-md transition hover:bg-blue-600"
          >
            Edit
          </button>
        )}
        <button
          onClick={handleDelete}
          className="bg-red-500 text-white p-2 rounded-md transition hover:bg-red-600"
        >
          Delete
        </button>
      </div>
    </div>
  );
};

export default Notepad;
